import { getAllFeedback, getFeedBackById ,deleteFeedbackByID,addFeedback,updateFeedback } from "../redux/FeedbackSlice";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { getFeedbackByIdService,getAllFeedbackService ,deleteFeedbackService,addFeedbackService,updateFeedbackService} from "../service/FeedbackService";
import FeedbackModel from "../model/FeedbackModel";

const Feedback = () => {

    const [fid, setFid] = useState('');
    const [deleteFeedback, setDeleteFeedback] = useState('');

    const [feedbackData, setFeedbackData] = useState(new FeedbackModel());
    const [feedback,setFeedback]=useState(new FeedbackModel());


    const dispatch = useDispatch();

    const feedbackDataFromStore = useSelector((state) => state.feedback.feedbackState);
    const feedbackList = useSelector((state) => state.feedback.feedbackList);
    const feedbackDelete = useSelector((state) => state.feedback.feedbackDelete);
    const feedbackAdd = useSelector((state) => state.feedback.feedbackAdd);

    const handleFeedbackData = (e) => {
        console.log(e);
        setFeedbackData({
            ...feedbackData,
            [e.target.name]: e.target.value
        });
    }

    const handleUpdate=(e)=>{
        console.log("update feedback");

        setFeedback({
            ...feedback,
            [e.target.name]:e.target.value
        });
       // console.log(e.target.value);
    }

    // const handleFeedback = (e) => {
    //     console.log('handleFeedback');
    //     setFid(e.target.value);
    // }

    // const handleDeleteFeedback = (e) => {
    //     console.log('handleDeleteFeedback');
    //     setDeleteFeedback(e.target.value);
    // }

    // const handleAddbank = (e) => {
    //     console.log(e.target.value);
    //     setNewBankObj({
    //         ...newBankObj,
    //         [e.target.name]: e.target.value
    //     }


    const submitGetFeedbackById = (evt) => {
        evt.preventDefault();
        console.log('submitGetFeedbackById');
        getFeedbackByIdService(feedbackData.feedBackId)
            .then((response) => {
                dispatch(getFeedBackById(response.data));             // Sending data to redux store

            })
            .catch(() => {
                alert(`Feedback with ${feedbackData.feedBackId} not found.`);
            });

        setFid('');
    }

    const submitGetAllFeedback = (evt) => {
        evt.preventDefault();
        console.log('submitGetAllFeedback');
        getAllFeedbackService ()
            .then((response) => {
                dispatch(getAllFeedback(response.data));
            })
            .catch(() => {
                alert(`Something is wrong!`);
            });
    }

    const submitDeleteFeedback = (evt) => {
        evt.preventDefault();
        console.log('submitDeleteFeedback');
        deleteFeedbackService(feedbackData.feedBackId)
            .then((response) => {
                alert(`Feedback deleted successfully.`)
                dispatch(deleteFeedbackByID(response.data));             // Sending data to redux store

            })
            .catch(() => {
                alert(`Feedback with Id ${feedbackData.feedBackId} not found.`);
            });

    }

    const submitAddFeedback = (evt) => {
        evt.preventDefault();
        console.log('submitAddFeedback');
        addFeedbackService(feedbackData)
            .then((response) => {
                alert(`feedback Added successfully.`)
                dispatch(addFeedback(response.data));             // Sending data to redux store

            })
            .catch(() => {
                alert(`Feedback with Id ${feedbackData.feedBackId} already present.`);
            });

    }

    const submitUpdateFeedback=(evt)=>{
        evt.preventDefault();
        console.log("submitUpdateFeedback");
        updateFeedbackService(feedback)
        .then((response)=>{
            dispatch(updateFeedback(response.data));
            alert(`feedback updated sucesfully`);
        })
        .catch(()=>{
            alert(`feedback is not found`);
        });
        
    }




    return (
        
        <div className="container">

<h1 className="display-4 text-primary mt-3 mb-3" >&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<title></title>FeedBack Component</h1>
          
          <div class="accordion" id="accordionExample">

           

  <div class="card">
    <div class="card-header" id="headingOne">
      <h2 class="mb-0">
        <button class="btn btn-link" type="button" data-toggle="collapse" data-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
        <h3>Find feedback by id</h3>
        </button>
      </h2>
    </div>

    <div id="collapseOne" class="collapse show" aria-labelledby="headingOne" data-parent="#accordionExample">
      <div class="card-body">
       
      <div className="col-12 border border-light shadow p-3 mb-5 bg-white">
                {/* <h3>Find feedback by id</h3> */}
                <form className="form form-group form-primary" onSubmit={submitGetFeedbackById}>
                    <input className="form-control mt-3" type="number" id="feedBackId" name="feedBackId" value={feedbackData.feedBackId} onChange={handleFeedbackData} placeholder="Enter Feedback Id" autoFocus required />
                    <input className="form-control mt-3 btn btn-primary" type="submit" value="Find Feedback" />
                </form>

                <table className="table table-light table-striped ">
                        <thead>
                            <tr>
                                <th>FeedBack Id</th>
                                <th>Comments</th>
                                <th>FeedbackDate</th>
                                <th>Overall_rating</th>
                                <th>Scheme_rating</th>
                                <th>Scheme_training_rating</th>
                               
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>{feedbackDataFromStore.feedBackId}</td>
                                <td>{feedbackDataFromStore.comments}</td>
                                <td>{feedbackDataFromStore.feedbackdate}</td>
                                <td>{feedbackDataFromStore.overallRating}</td>
                                <td>{feedbackDataFromStore.schemeRating}</td>
                                <td>{feedbackDataFromStore.schemeTrainingRating}</td>

                            </tr>
                        </tbody>
                    </table>
                                    
            </div>
            <hr />
            
      </div>
    </div>
  </div>
   {/* ---------------------------------------------------------------------------------- */}

   <div class="card">
    <div class="card-header" id="headingTwo">
      <h2 class="mb-0">
        <button class="btn btn-link collapsed" type="button" data-toggle="collapse" data-target="#collapseTwo" aria-expanded="false" aria-controls="collapseTwo">
        <h3>Find all Feedback</h3>
        </button>
      </h2>
    </div>
    <div id="collapseTwo" class="collapse" aria-labelledby="headingTwo" data-parent="#accordionExample">
      <div class="card-body">
       
      <div>
                <div className="col-12 border border-light shadow p-3 mb-5 bg-white">
                    {/* <h3>Find all Feedback</h3> */}
                    <div>
                        <form className="form form-group form-primary">
                            <input className="mt-3 btn btn-primary btn-block" type="button" onClick={submitGetAllFeedback} value="Find All Feedback" />
                        </form>
                    </div>
                    <table className="table table-light table-striped ">
                        <thead>
                            <tr>
                                <th>FeedBack Id</th>
                                <th>Comments</th>
                                <th>FeedbackDate</th>
                                <th>Overall_rating</th>
                                <th>Scheme_rating</th>
                                <th>Scheme_training_rating</th>
                            </tr>
                        </thead>
                        <tbody>
                            {feedbackList.map((feedback, key) => {
                                return (
                                    <tr key={key}>  <td>{feedback.feedBackId}</td>
                                        <td>{feedback.comments}</td>
                                        <td>{feedback.feedbackdate}</td>
                                        <td>{feedback.overallRating}</td>
                                        <td>{feedback.schemeRating}</td>
                                        <td>{feedback.schemeTrainingRating}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div>
            </div>


            <hr />

      </div>
    </div>
  </div>
  {/* ---------------------------------------------------------------------------------- */}

  <div class="card">
    <div class="card-header" id="headingThree">
      <h2 class="mb-0">
        <button class="btn btn-link collapsed" type="button" data-toggle="collapse" data-target="#collapseThree" aria-expanded="false" aria-controls="collapseThree">
        <h3>Delete feedback by Id</h3>
        </button>
      </h2>
    </div>
    <div id="collapseThree" class="collapse" aria-labelledby="headingThree" data-parent="#accordionExample">
      <div class="card-body">
        

      <div className="col-12 border border-light shadow p-3 mb-5 bg-white">
                {/* <h3>Delete feedback by Id</h3> */}
                <form className="form form-group form-primary" onSubmit={submitDeleteFeedback}>
                    <input className="form-control mt-3" type="number" id="feedBackId" name="feedBackId" value={feedbackData.feedBackId} onChange={ handleFeedbackData} placeholder="Enter Feedback Id" autoFocus required />
                    <input className="form-control mt-3 btn btn-danger" type="submit" value="Delete Feedback" />
                </form>

                <table className="table table-light table-striped ">
                    <thead>
                        <tr>
                                <th>FeedBack Id</th>
                                <th>Comments</th>
                                <th>FeedbackDate</th>
                                <th>Overall_rating</th>
                                <th>Scheme_rating</th>
                                <th>Scheme_training_rating</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                                <td>{feedbackDelete.feedBackId}</td>
                                <td>{feedbackDelete.comments}</td>
                                <td>{feedbackDelete.feedbackdate}</td>
                                <td>{feedbackDelete.overallRating}</td>
                                <td>{feedbackDelete.schemeRating}</td>
                                <td>{feedbackDelete.schemeTrainingRating}</td>

                        </tr>
                    </tbody>
                </table>

            </div>

            <hr />

      </div>
    </div>
  </div>
  {/* ---------------------------------------------------------------------------------- */}

  <div class="card">
    <div class="card-header" id="headingTwo">
      <h2 class="mb-0">
        <button class="btn btn-link collapsed" type="button" data-toggle="collapse" data-target="#collapseTwo" aria-expanded="false" aria-controls="collapseTwo">
        <h3>Add Feedback</h3>
        </button>
      </h2>
    </div>
    <div id="collapseTwo" class="collapse" aria-labelledby="headingTwo" data-parent="#accordionExample">
      <div class="card-body">
        
